import React, { useState } from "react";

export default function SearchBar({onSearch}) {
 const [ciudad, setCiudad] = useState("");
  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      onSearch(ciudad);
    }}>
      <input
        type="text"
        placeholder="Ciudad..."
      />
      <input type="submit" value="Agregar" />
    </form>
  );
}
